mapper.select:
SELECT M.MEMBER_CODE , M.MEMBER_NAME , M.MEMBER_AGE , M.BACK_NUMBER , M.POSITION , M.HEIGHT , M.WEIGHT , M.TEAM_CODE , T.TEAM_NAME , T.REGION FROM MEMBER M, TEAM T
M.TEAM_CODE = T.TEAM_CODE
AND M.POSITION = #{ position }

SELECT M.MEMBER_CODE , M.MEMBER_NAME , M.MEMBER_AGE , M.BACK_NUMBER , M.POSITION , M.HEIGHT , M.WEIGHT , M.TEAM_CODE , T.TEAM_NAME , T.REGION FROM MEMBER M, TEAM T
M.TEAM_CODE = T.TEAM_CODE
AND M.TEAM_CODE = #{teamCode}

mapper.update:
UPDATE MEMBER


        MEMBER_AGE = #{ memberAge}


        ,BACK_NUMBER = #{ backNumber }


        ,HEIGHT = #{ height }


        ,WEIGHT = #{ weight }


        ,POSITION = #{ position }


        ,TEAM_CODE = #{ teamCode }


WHERE MEMBER_CODE = #{ memberCode }

mapper.delete:
DELETE
FROM MEMBER
WHERE MEMBER_CODE = #{ memberCode }
